import React, { useState } from "react";
import { StyleSheet, Text, TextInput, View, Button } from 'react-native';
import { Formik } from "formik";
import {Close, Done } from '@mui/icons-material';
import { TextField, Box, AppBar, Toolbar, IconButton, } from "@mui/material";

export default function Form({ setModalWindow, setListOfItems, item }) {
  //const item = {};
  return (
    <Box sx={{flexGrow: 1}} >
      <AppBar position='fixed'>
        <Toolbar sx={{ justifyContent: 'space-between'}} > 
          <IconButton edge='start' color='inherit' aria-label='menu' sx={{ mr: 2 }} onClick={() => {setModalWindow(false)}} >
            <Close />
          </IconButton>
          <Text style={styles.header} >Новая смена</Text>
          <IconButton edge='end' color='inherit' aria-label='done' sx={{ mr: 2 }} onClick={() => {setModalWindow(false)}} >
            <Done />
          </IconButton>
        </Toolbar>
      </AppBar>
      <Toolbar />
      <Formik initialValues={{ item }} onSubmit={(values) => {
        console.log(values)
      }}>
        {(props) => (
          <View>
            <TextField id='outlined-basic' label='outlined-basic' variant="outlined" />
          </View>
        )}
      </Formik>

    </Box>

  );
}

const styles = StyleSheet.create({
  text: {
    borderWidth: 1,
    borderStyle: 'solid',
    borderColor: '#000',
    padding: 10,
    marginVertical: 30,
    marginHorizontal: '20%',
    width: '60%'
  },
  header: {
    fontSize: 22,
    color: 'white'
  },
});
