import React from "react";
import { StyleSheet, Text, View } from 'react-native';

export default function Detail( {route} ) {
    const item = route.params.item;
    const settings = route.params.settings;
    //console.log(item);
    return (       
      <View style={styles.main}>
        <Text style={styles.header}>Цена топлива {settings.priceFuel} | Средний расход {settings.averageFuel}</Text>
        <Text style={styles.text}>Дата: {item.data}</Text>
        <Text style={styles.text}>Спидометр на начало: {item.odometerStart}</Text>
        <Text style={styles.text}>Спидометр на конец: {item.odometerFinish}</Text>
        <Text style={styles.text}>Пробег: {item.odometer}</Text>
        <Text style={styles.text}>Выручка: {item.proceeds}</Text>
        <Text style={styles.text}>Расходы: {item.expenses}</Text>
        <Text style={styles.profit}>Доход: {item.profit}</Text>
      </View>
    );
  }
  
  const styles = StyleSheet.create({
    main: {
      flex: 1,
      //paddingTop: 20,      
    },
    header:{
        fontSize: 20,
        backgroundColor: '#E0FFFF',
        textAlign: 'center',
        paddingVertical: 20 
    },
    profit: {
        fontSize: 20,
        textAlign: 'left',
        paddingVertical: 15,
        backgroundColor: '#98FB98',
        paddingHorizontal: 10,
    },
    text:{
      fontSize: 18,
      textAlign: 'left',
      marginLeft: 10, 
      paddingVertical: 15
    }
  });
