import React, {useState} from "react";
import { StyleSheet, Text, View, TouchableOpacity, FlatList } from 'react-native';


export default function Main({ navigation }) {
    const loadScene = () => {
      navigation.navigate('Detail');
    }
    const [listOfItems, setListOfItems] = useState([
      {data: '15.06.23', proceeds: 150, profit: 100, odometerStart: 102, odometerFinish: 150, odometer: 48, key: '1'},
      {data: '16.06.23', proceeds: 500, profit: 400, odometerStart: 170, odometerFinish: 200, odometer: 48, key: '2'},
      {data: '17.06.23', proceeds: 300, profit: 200, odometerStart: 210, odometerFinish: 260, odometer: 48, key: '3'},
      {data: '18.06.23', proceeds: 400, profit: 300, odometerStart: 300, odometerFinish: 344, odometer: 48, key: '4'}
    ]);
    return (
      <View style={styles.main}>
        <Text style={styles.text}>Список смен</Text>
        <FlatList data={listOfItems} renderItem={({item}) => (
          <TouchableOpacity onPress={() => navigation.navigate('Detail', { item })}>
              <Text>{ item.data} </Text>
              <Text>{ item.proceeds} </Text>
          </TouchableOpacity>
        )}
        />
      </View>
    );
  }
  
  const styles = StyleSheet.create({
    main: {
      flex: 1,
      padding: 20,
      //height: 100,
      //backgroundColor: 'silver',
    },
    text:{
      fontSize: 24,
      color: 'red',
      textAlign: 'center'
    }
  });
