import React, {useState} from "react";
import { StyleSheet, Text, View, TouchableOpacity, FlatList, Modal } from 'react-native';
import { Accordion, AccordionSummary, AccordionDetails } from "@mui/material";
import ExpandMoreIcon from '@mui/icons-material/ExpandMore'
import Form from "./Form";
import { AntDesign } from '@expo/vector-icons';

export default function Main({settings}) {
    const loadScene = () => {
      navigation.navigate('Detail');
    }
    const [modalWindow, setModalWindow] = useState(false);

    //const settings = settings;
    const [listOfItems, setListOfItems] = useState([
      {   data: '15.06.23',
          proceeds: 150, //выручка
          profit: 100,    //доход
          odometerStart: 102, //спидометр старт
          odometerFinish: 150, //спидометр финиш
          odometer: 48,     //пробег
          expenses: 50,     //затраты
          key: '1'
      },
      {data: '16.06.23', proceeds: 500, profit: 400, odometerStart: 170, odometerFinish: 200, odometer: 48, key: '2'},
      {data: '17.06.23', proceeds: 300, profit: 200, odometerStart: 210, odometerFinish: 260, odometer: 48, key: '3'},
      {data: '18.06.23', proceeds: 400, profit: 300, odometerStart: 300, odometerFinish: 344, odometer: 48, key: '4'}
    ]);
    console.log(settings);    
    

    return (
      <View style={styles.main}> 
        <FlatList data={listOfItems} renderItem={({item}) =>
            <Accordion>
              <AccordionSummary
                expandIcon={<ExpandMoreIcon/>}
                aria-controls="panel1a-content"
                id='panel1a-header'
              >
                <Text style={styles.item} >{ item.data} Выручка: { item.proceeds } Пробег: { item.profit }</Text>
              </AccordionSummary>
              <AccordionDetails>
                <View>
                  <Text style={styles.text}>Цена топлива {settings.priceFuel} | Средний расход {settings.averageFuel}</Text>
                  <Text style={styles.text}>Дата: {item.data}</Text>
                  <Text style={styles.text}>Спидометр на начало: {item.odometerStart}</Text>
                  <Text style={styles.text}>Спидометр на конец: {item.odometerFinish}</Text>
                  <Text style={styles.text}>Пробег: {item.odometer}</Text>
                  <Text style={styles.text}>Выручка: {item.proceeds}</Text>
                  <Text style={styles.text}>Расходы: {item.expenses}</Text>
                  <Text style={styles.text}>Доход: {item.profit}</Text>
                </View>
                
              </AccordionDetails>
            </Accordion> 
          }/>
      </View>
    );
  }
 
  const styles = StyleSheet.create({
    main: {
      //flex: 1,
      //padding: 10,
      //height: 100,
      //backgroundColor: 'silver',
    },
    text:{
      fontSize: 18,
      textAlign: 'left',
      //marginLeft: 10, 
      //paddingVertical: 15
    },
    header:{
      fontSize: 20,
      //backgroundColor: '#E0FFFF',
      //textAlign: 'center',
      //paddingVertical: 20 
    },
    profit: {
        fontSize: 20,
        //textAlign: 'left',
        //paddingVertical: 15,
        //backgroundColor: '#98FB98',
        //paddingHorizontal: 10,
    },
    item: {
      fontSize: 22,
      textAlign: 'left',
      //borderWidth: 1,
      //borderRadius: 5,
      //borderColor: '#000',
      padding: 5,
      //marginVertical: 5,
      //marginHorizontal: '1%',
      //width: '98%'
    },
    iconAdd:{
      textAlign: 'center',
      marginVertical: 15
    }
  });
