import Form from './components/Form';
import { useState } from 'react';

export default function App() {
  const [listOfItems, setListOfItems] = useState([
    {text: '1 смена', index: '1'},
    {text: '2 смена', index: '2'},
    {text: '3 смена', index: '3'},
    {text: '4 смена', index: '4'}
  ])
const addHandler = (text) => {
  setListOfItems((list) => {
    console.log(text);
    return [
      {text: text, index: 5},
      ...list
    ]
  })
}

  return (
    <MainStack/>
  );
}
