import React, {useState} from "react";
import { StyleSheet, Text, View, TouchableOpacity, FlatList, Modal } from 'react-native';
import { Accordion, AccordionSummary, AccordionDetails, Stack, IconButton } from "@mui/material";
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import { Edit, Delete} from '@mui/icons-material';

export default function Main({settings}) {
    const loadScene = () => {
      navigation.navigate('Detail');
    }
    const [modalWindow, setModalWindow] = useState(false);

    //const settings = settings;
    const [listOfItems, setListOfItems] = useState([
      {   data: '15.06.23',
          proceeds: 150, //выручка
          profit: 100,    //доход
          odometerStart: 102, //спидометр старт
          odometerFinish: 150, //спидометр финиш
          odometer: 48,     //пробег
          expenses: 50,     //затраты
          key: '1'
      },
      {data: '16.06.23', proceeds: 500, profit: 400, odometerStart: 170, odometerFinish: 200, odometer: 48, key: '2'},
      {data: '17.06.23', proceeds: 300, profit: 200, odometerStart: 210, odometerFinish: 260, odometer: 48, key: '3'},
      {data: '18.06.23', proceeds: 400, profit: 300, odometerStart: 300, odometerFinish: 344, odometer: 48, key: '4'}
    ]);
    console.log(settings);    
    

    return (
        <FlatList data={listOfItems} renderItem={({item}) =>
            <Accordion>
              <AccordionSummary
                expandIcon={<ExpandMoreIcon/>}
                aria-controls="panel1a-content"
                id='panel1a-header'
              >
                <Text style={styles.summary} >{ item.data} Выручка: { item.proceeds } Пробег: { item.odometer }</Text>
              </AccordionSummary>
              <AccordionDetails>
                <View>
                  <Text style={styles.detail}>Цена топлива {settings.priceFuel} | Средний расход {settings.averageFuel}</Text>
                  <Text style={styles.detail}>Расходы: {item.expenses}</Text>
                  <Text style={styles.detail}>Доход: {item.profit}</Text>
                  <Stack direction='row' spacing={1}>
                    <IconButton edge='start' color='inherit' aria-label='menu' sx={{mr: 2}}>
                      <Edit />
                    </IconButton>
                    <IconButton edge='start' color='inherit' aria-label='menu' sx={{mr: 2}}>
                      <Delete />
                    </IconButton>
                  </Stack>
                </View>
                
              </AccordionDetails>
            </Accordion> 
          }/>
    );
  }
 
  const styles = StyleSheet.create({
    main: {
      //flex: 1,
      paddingTop: 0,
      //height: 100,
      //backgroundColor: 'silver',
    },
    detail:{
      fontSize: 18,
      textAlign: 'left',
      //marginLeft: 10, 
      paddingVertical: 5
    },
    header:{
      fontSize: 20,
      //backgroundColor: '#E0FFFF',
      //textAlign: 'center',
      //paddingVertical: 20 
    },
    profit: {
        fontSize: 18,
        //textAlign: 'left',
        //paddingVertical: 15,
        //backgroundColor: '#98FB98',
        //paddingHorizontal: 10,
    },
    summary: {
      fontSize: 18,
      fontStyle: '400',
      textAlign: 'left',
    },
    iconAdd:{
      textAlign: 'center',
      marginVertical: 15
    }
  });
