import React, { useState } from "react";
import { StyleSheet, Text, TextInput, View, Button } from 'react-native';

export default function Form() {
    const [text, setValue ] = useState('');

    const onChange = (text) => {
        setValue(text);
    };

    return (
      <View>
        <TextInput style={styles.text} onChangeText={onChange}  />
        <Button color='green' onPress={() => addHandler(text)} title='Добавить смену'/>
      </View>
    );
  }
  
  const styles = StyleSheet.create({
    text: {
        borderBottomWidth: 1,
        borderColor: '#000',
        padding: 10,
        marginVertical: 30,
        marginHorizontal: '20%',
        width: '60%'
    }
  });
